package unit1;
/**
 * Description: This program shows instructions for the game BINGO and an example for a bingo card.
 * Date: September 27, 2024
 * @author Daniella Pefanis
 */

public class Bingo {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("How to play BINGO!!!!");
		System.out.println("");
		System.out.println("Step 1: Be prepared to have fun and test your luck!");
		System.out.println("Step 2: The caller randomly pulls a numbered bingo ball.");
		System.out.println("Step 3: The number is placed on the bingo card and called out.");
		System.out.println("Step 4: Players look for that called number on their bingo card.");
		System.out.println("Step 5: If the player has that number, it's marked off.");
		System.out.println("Step 6: Steps 2-5 are repeated until a player matches the BINGO pattern.");
		System.out.println("Step 7: The winning player must shout BINGO! to officially win the game.");
		System.out.println("-------------------------------------------------------------------------");
		System.out.println("BINGO Game Card Example:");
		System.out.println("");
		System.out.println("---------------------------------------------------");
		System.out.format("%10s%10s%10s%10s%10s", "B","I","N","G","O");
		System.out.format("\n%10s%10s%10s%10s%10s", "8","23","34","57","72");
		System.out.format("\n%10s%10s%10s%10s%10s", "3","29","43","60","64");
		System.out.format("\n%10s%10s%10s%10s%10s", "10","17","FREE","48","70");
		System.out.format("\n%10s%10s%10s%10s%10s", "1","30","45","59","75");
		System.out.format("\n%10s%10s%10s%10s%10s", "2","15","33","46","61");
		System.out.format("\n---------------------------------------------------");
		System.out.println("\nHAVE FUN!!");
	}

}
